    constructor(props) {
        super(props);
        this.plugin = null;
    }

    componentDidMount() {
        //
    }



    render() {
        return (
            <>
                <div className="container">
                    <div id="unity-container" className="panel" ref={(el) => this.unityContainer = el}></div>
                    <div className="panel">
                    </div>
                </div>
                <style jsx>
                {`
                .container {
                    background-color: white;
                    position: absolute;
                    width: 100vw;
                    height: 100vh;
                    left: 0px;
                    top: 0px;
                    display: grid;
                    grid-template-columns: auto 300px;
                    overflow: hidden;
                }
                .panel {
                    background-color: lightpink;
                }
                `}
                </style>
            </>
        )
    }
}
