import React from 'react';
import ShaneLoader from './ShaneLoader';
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.plugin = null;

        this.state = {
            title: "Super Pants",
        }

        this.onPlay = this.onPlay.bind(this);
        this.onStop = this.onStop.bind(this);

        this.onSetTitle = this.onSetTitle.bind(this);

        this.onGetColor = this.onGetColor.bind(this);
        this.onSetColor = this.onSetColor.bind(this);

        this.handleChange = this.handleChange.bind(this);

        this.handleProperty = this.handleProperty.bind(this);

    }
    componentDidMount() {

        //console.log(this.appContainer.offsetHeight);

        const ret = ShaneLoader(this.appContainer);
        this.plugin = ret.plugin();

        this.plugin.OnRotationPlay = this.onRotationPlay;
        this.plugin.OnRotationStop = this.onRotationStop;
        this.plugin.OnChangeColor = this.onColorChange;

        const title = this.plugin.GetTitle();
        console.log("title", title);

        /*this.setState({
            title: title,
        })*/
    }

    onRotationPlay() {
        console.log("rotation play")
    }
    onRotationStop() {
        console.log("rotation stop")
    }
    onColorChange(color) {
        console.log("change color: " + color)
    }

    onPlay() {
        this.plugin.Play();
    }
    onStop() {
        this.plugin.Stop();
    }

    onSetColor() {
        this.plugin.SetTankColor("#80BFFF");
    }
    onGetColor() {
        const color = this.plugin.GetTankColor();
        console.log("color", color);
    }

    onSetTitle() {
        this.plugin.SetTitle("PANZER PANTS");
    }

    handleProperty() {

        const rank = this.plugin.GetTankProperty("Rank");
        const power = this.plugin.GetTankProperty("Power");
        const armor = this.plugin.GetTankProperty("Armor");
        const speed = this.plugin.GetTankProperty("Speed");

        console.log("property", rank, power, armor, speed);

    }

    handleChange(e) {

        console.log(e.target.value);

        this.setState({
            title: e.target.value,
        })

    }

    render() {
        return (
            <>
                <div className="react-app-container" ref={(el) => this.appContainer = el}>
                    <div className="control-panel">
                        <input type="text" onChange={this.handleChange} value={this.state.title} />
                    </div>
                    <div className="property-panel">

                    </div>
                </div>
                <style jsx>
                {`
                    .react-app-container {
                        background-color: lightcyan;
                        position: relative;
                        width: calc(100% - 10px);
                        height: calc(100% - 10px);
                        display: grid;
                        grid-template-rows: 200px auto;
                        grid-gap: 5px;
                        padding: 5px;
                    }
                    .react-app-container div {
                        background-color: lightpink;
                    }
                `}
                </style>
            </>
        )
    }
}
