import React from 'react';
import UnityLoader from '../components/UnityLoader';
import PropertyItem from '../components/PropertyItem';
//import App from '../components/App';

export default class Index extends React.Component {
    constructor(props) {

        super(props);

        this.plugin = null;

        this.state = {
            title: "",
            rank: 0,
            power: 0,
            armor: 0,
            speed: 0,
            color: "",
        }


        this.OnRotationPlay = this.OnRotationPlay.bind(this);
        this.OnRotationStop = this.OnRotationStop.bind(this);
        this.OnColorChange = this.OnColorChange.bind(this);
        this.OnLoad = this.OnLoad.bind(this);

    }

    componentDidMount() {
        const ret = UnityLoader(this.unityContainer);
        this.plugin = ret.getPluginContext();

        this.plugin.OnRotationPlay = this.OnRotationPlay;
        this.plugin.OnRotationStop = this.OnRotationStop;
        this.plugin.OnChangeColor = this.OnColorChange;
        this.plugin.OnLoad = this.OnLoad;

    }

    OnLoad() {

        const title = this.plugin.GetTitle();
        const rank = this.plugin.GetRank();
        const power = this.plugin.GetPower();
        const armor = this.plugin.GetArmor();
        const speed = this.plugin.GetSpeed();

        this.setState({
            title: title,
            rank: rank,
            power: power,
            armor: armor,
            speed: speed,
        })
    }

    OnRotationPlay() {
        //
    }
    OnRotationStop() {
        //
    }
    OnColorChange(color) {
        //
    }

    render() {

        const item_titles = ["Super Tank", "Tu-Tank-Hamon", "Tank You", "No Tanks"];
        const item_ranks = [1, 2, 3, 4, 5];
        const item_powers = [25, 50, 75, 100];
        const item_armors = [10, 20, 30, 40, 50];
        const item_speed = [5, 25, 45, 65, 85];
        const item_colors = ["#df80ff", "#80b3ff", "#00e6ac", "#ff8c1a", "#88cc00"];

        return (
            <>
                <div className="container">
                    <div id="unity-container" ref={(el) => this.unityContainer = el}></div>
                    <div id="app-container">
                        <div className="property-panel">
                            <h4>Properties</h4>
                            <div className="subpanel">
                                <PropertyItem label="Title" items={item_titles} />
                                <PropertyItem label="Rank" items={item_ranks} />
                                <PropertyItem label="Power" items={item_powers} />
                                <PropertyItem label="Armor" items={item_armors} />
                                <PropertyItem label="Speed" items={item_speed} />
                                <PropertyItem label="Color" items={item_colors} />
                            </div>
                        </div>
                        <div className="event-panel">
                            <h4>Events</h4>
                            <div className="subpanel">
                                <div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <style jsx>
                {`
                .container {
                    background-color: white;
                    position: absolute;
                    width: 100vw;
                    height: 100vh;
                    left: 0px;
                    top: 0px;
                    display: grid;
                    grid-template-columns: auto 300px;
                    overflow: hidden;
                }
                #app-container {
                    background-color: white;
                    position: relative;
                    width: calc(100% - 10px);
                    height: calc(100% - 10px);
                    display: grid;
                    grid-template-rows: 180px auto;
                    grid-gap: 5px;
                    padding: 5px;
                }
                h4 {
                    background-color: transparent;
                    margin: 0px;
                    color: crimson;
                }
                p {
                    margin: 0px;
                }
                .property-panel {
                    position: relative;
                    width: 100%;
                    height: 100%;
                    display: grid;
                    grid-template-rows: 20px auto;
                }
                .event-panel {
                    position: relative;
                    width: 100%;
                    height: 100%;
                    display: grid;
                    grid-template-rows: 20px auto;
                    overflow: auto;
                }
                .subpanel {
                    background-color: lightpink;
                }
                .subpanel div {
                    background-color: white;
                    padding: 5px;
                    overflow: auto;
                }
                `}
                </style>
            </>
        )
    }
}
