export default containerElement => {

    //console.log(containerElement.offsetWidth, containerElement.offsetHeight);

    (function(window){

        'use strict';

        function unityPlugin() {
            var tdx = {};
            var MAX_INDEX = 100000;
		    var retval = Array(MAX_INDEX + 1).fill();
            var retindex = 0;

            function getRetIndex()
            {
                return (retindex >= MAX_INDEX)?0:retindex++;
            }
            function getRetValue(index)
            {
                var value = retval[index];
                delete retval[index];
                return value;
            }
            function sendMessage(func_name, param)
            {
                if (typeof gameInstance === "undefined") {
                    //throw "Calling:" + func_name;
                    console.log("TDX : " + func_name);
                    //throw new Error("hey jude");
                    return;
                }

                console.log("SEND MESSAGE : " + func_name);
                gameInstance.SendMessage("SceneManager", func_name, param);
                return true;
            }
            function makeParam()
            {
                var param = {
                    index: arguments[0],
                    args: null
                };

                if (arguments.length > 1 && arguments[1] !== null)
                {
                    console.log("111")
                    //param.args = JSON.stringify(Object.values(arguments[1]));
                    var _args = arguments[1];
                    //console.log(_args)
                    param.args = String(_args[0]);
                }

                console.log("222", param)

                const sendData = JSON.stringify(param);
                //return JSON.stringify(param);
                console.log(sendData);

                return sendData;
            }

            // Utility
            tdx.SetRetValue = function(index, value)
            {
                retval[index] = value;
            };

            // Events
            tdx.OnLoad = function(callback)
            {
                return callback;
            };
            tdx._OnLoad = function()
            {
                this.OnLoad();
            };

            tdx.OnDeselect = function(callback)
            {
                return callback;
            };
            tdx._OnDeselect = function()
            {
                this.OnDeselect();
            };

            tdx.OnClick = function(callback)
            {
                return callback;
            };
            tdx._OnClick = function(attr)
            {
                this.OnClick(attr);
            };

            // Methods
            tdx.GetTankProperty = function()
            {
                var index = getRetIndex();
                sendMessage("GetTankProperty", makeParam(index, arguments));
                return getRetValue(index);
            };

            tdx.GetTitle = function()
            {
                var index = getRetIndex();
                sendMessage("GetTitle", makeParam(index));
                return getRetValue(index);
            };
            tdx.SetTitle = function()
            {
                sendMessage("SetTitle", makeParam(-1, arguments));
            };

            tdx.SetPower = function()
            {
                sendMessage("SetPower", makeParam(-1, arguments));
            };

            tdx.Play = function()
            {
                sendMessage("Play", makeParam(-1, arguments));
            };

            tdx.Stop = function()
            {
                sendMessage("Stop", makeParam(-1, arguments));
            };

            Object.defineProperty(tdx.SetRetValue, "name", { value: "SetRetValue" });

            return tdx;

        }

        if(typeof(window.TdxPlayer) === 'undefined'){
            window.TdxPlayer = unityPlugin();
        }

        var gameInstance = UnityLoader.instantiate("unity-container", "/data/Build.json");


    })(window);

    function foo() {
        console.log("foo");
    }

    function plugin() {
        return window.TdxPlayer;
    }

    return {
        foo,
        plugin,
    }
}
