import React from 'react';
import UnityLoader from '../components/UnityLoader';
//import App from '../components/App';

export default class Index extends React.Component {
    constructor(props) {

        super(props);

        this.plugin = null;

        this.state = {
            title: "",
            rank: 0,
            power: 0,
            armor: 0,
            speed: 0,
            color: "",
        }


        this.OnRotationPlay = this.OnRotationPlay.bind(this);
        this.OnRotationStop = this.OnRotationStop.bind(this);
        this.OnColorChange = this.OnColorChange.bind(this);
        this.OnLoad = this.OnLoad.bind(this);

    }

    componentDidMount() {
        const ret = UnityLoader(this.unityContainer);
        this.plugin = ret.getPluginContext();

        this.plugin.OnRotationPlay = this.OnRotationPlay;
        this.plugin.OnRotationStop = this.OnRotationStop;
        this.plugin.OnChangeColor = this.OnColorChange;
        this.plugin.OnLoad = this.OnLoad;

    }

    OnLoad() {

        const title = this.plugin.GetTitle();

        const rank = this.plugin.GetRank();
        const power = this.plugin.GetPower();
        const armor = this.plugin.GetArmor();
        const speed = this.plugin.GetSpeed();


    }

    OnRotationPlay() {
        //
    }
    OnRotationStop() {
        //
    }
    OnColorChange(color) {
        //
    }

    render() {
        return (
            <>
                <div className="container">
                    <div id="unity-container" ref={(el) => this.unityContainer = el}></div>
                    <div id="app-container">
                        <div className="property-panel">
                            <h4>Properties</h4>
                            <div className="subpanel"></div>
                        </div>
                        <div className="event-panel">
                            <h4>Events</h4>
                            <div className="subpanel">
                                <div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <style jsx>
                {`
                .container {
                    background-color: white;
                    position: absolute;
                    width: 100vw;
                    height: 100vh;
                    left: 0px;
                    top: 0px;
                    display: grid;
                    grid-template-columns: auto 300px;
                    overflow: hidden;
                }
                #app-container {
                    background-color: white;
                    position: relative;
                    width: calc(100% - 10px);
                    height: calc(100% - 10px);
                    display: grid;
                    grid-template-rows: 180px auto;
                    grid-gap: 5px;
                    padding: 5px;
                }
                h4 {
                    background-color: transparent;
                    margin: 0px;
                    color: crimson;
                }
                p {
                    margin: 0px;
                }
                .property-panel {
                    position: relative;
                    width: 100%;
                    height: 100%;
                    display: grid;
                    grid-template-rows: 20px auto;
                }
                .event-panel {
                    position: relative;
                    width: 100%;
                    height: 100%;
                    display: grid;
                    grid-template-rows: 20px auto;
                    overflow: auto;
                }
                .subpanel {
                    background-color: lightpink;
                }
                .subpanel div {
                    background-color: white;
                    padding: 5px;
                    overflow: auto;
                }
                `}
                </style>
            </>
        )
    }
}
