import React from 'react';
import ShaneLoader from './ShaneLoader';
import EventItem from './EventItem';
import PropertyItem from './PropertyItem';

export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.plugin = null;

        this.state = {
            title: "Super Pants",
            rank: 0,
            power: 0,
            armor: 0,
            speed: 0,
            color: '',
            eventItems: [],
        }

        this.onPlay = this.onPlay.bind(this);
        this.onStop = this.onStop.bind(this);

        this.onSetTitle = this.onSetTitle.bind(this);

        this.onGetColor = this.onGetColor.bind(this);
        this.onSetColor = this.onSetColor.bind(this);

        this.handleChange = this.handleChange.bind(this);

        this.handleProperty = this.handleProperty.bind(this);

        this.onRotationPlay = this.onRotationPlay.bind(this);
        this.onRotationStop = this.onRotationStop.bind(this);
        this.onColorChange = this.onColorChange.bind(this);

    }
    componentDidMount() {

        //console.log(this.appContainer.offsetHeight);

        const ret = ShaneLoader(this.appContainer);
        this.plugin = ret.plugin();

        this.plugin.OnRotationPlay = this.onRotationPlay;
        this.plugin.OnRotationStop = this.onRotationStop;
        this.plugin.OnChangeColor = this.onColorChange;

        //const title = this.plugin.GetTitle();
        //console.log("titles", title);

        /*this.setState({
            title: title,
        })*/
    }

    onRotationPlay() {
        console.log("rotation play")

        const newEvent = {
            text: "play",
            color: "#ddd",
        }

        const items = this.state.eventItems||[];
        items.push(newEvent);

        this.setState({
            eventItems: items,
        });
    }
    onRotationStop() {
        console.log("rotation stop")

        const newEvent = {
            text: "stop",
            color: "#aaa",
        }

        const items = this.state.eventItems||[];
        items.push(newEvent);

        this.setState({
            eventItems: items,
        });
    }
    onColorChange(color) {
        console.log("change color: " + color)

        const newEvent = {
            text: "color",
            color: color,
        }

        const items = this.state.eventItems||[];
        items.push(newEvent);

        this.setState({
            eventItems: items,
        });

    }

    onPlay() {
        this.plugin.Play();
    }
    onStop() {
        this.plugin.Stop();
    }

    onSetColor() {
        this.plugin.SetTankColor("#80BFFF");
    }
    onGetColor() {
        const color = this.plugin.GetTankColor();
        console.log("color", color);
    }

    onSetTitle() {
        this.plugin.SetTitle("PANZER PANTS");
    }

    handleProperty() {

        const rank = this.plugin.GetTankProperty("Rank");
        const power = this.plugin.GetTankProperty("Power");
        const armor = this.plugin.GetTankProperty("Armor");
        const speed = this.plugin.GetTankProperty("Speed");

        console.log("property", rank, power, armor, speed);

    }

    handleChange(e) {

        console.log(e.target.value);

        this.setState({
            title: e.target.value,
        })

    }

    render() {
        return (
            <>
                <div className="react-app-container" ref={(el) => this.appContainer = el}>

                    <div className="control-panel">
                        <h4>Properties</h4>
                        <div>
                            <PropertyItem label="Title" />
                            <PropertyItem label="Rank" />
                            <PropertyItem label="Power" />
                            <PropertyItem label="Armor" />
                            <PropertyItem label="Speed" />
                            <PropertyItem label="Color" />
                        </div>
                    </div>

                    <div className="property-panel">
                        <h4>Events</h4>
                        <div>
                        {
                            this.state.eventItems.length > 0 && this.state.eventItems.map((item, index) => {
                                return (
                                    <EventItem key={index} text={item.text} color={item.color} />
                                )
                            })
                        }   
                        </div>
                    </div>

                </div>
                <style jsx>
                {`
                    .react-app-container {
                        background-color: white;
                        position: relative;
                        width: calc(100% - 10px);
                        height: calc(100% - 10px);
                        display: grid;
                        grid-template-rows: 180px auto;
                        grid-gap: 5px;
                        padding: 5px;
                    }
                    .react-app-container div {
                        background-color: white;
                    }
                    .react-app-container h4 {
                        background-color: transparent;
                        margin: 0px;
                        color: crimson;
                    }
                    .control-panel {
                        position: relative;
                        width: 100%;
                        height: 100%;
                        display: grid;
                        grid-template-rows: 20px auto;
                    }
                    .control-panel div {
                        background-color: white;
                        padding: 5px;
                        overflow: auto;
                    }
                    .property-panel {
                        position: relative;
                        width: 100%;
                        height: 100%;
                        display: grid;
                        grid-template-rows: 20px auto;
                    }
                    .property-panel div {
                        background-color: white;
                        padding: 5px;
                        overflow: auto;
                    }
                `}
                </style>
            </>
        )
    }
}
