import React from 'react';
import ShaneLoader from './ShaneLoader';
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.plugin = null;

        this.state = {
            title: "Super Pants",
        }

        this.onPlay = this.onPlay.bind(this);
        this.onStop = this.onStop.bind(this);

        this.onSetTitle = this.onSetTitle.bind(this);
        this.onSetColor = this.onSetColor.bind(this);

        this.handleChange = this.handleChange.bind(this);

        this.handleProperty = this.handleProperty.bind(this);

    }
    componentDidMount() {

        //console.log(this.appContainer.offsetHeight);
        const ret = ShaneLoader(this.appContainer);
        this.plugin = ret.plugin();

        this.plugin.OnLoad = this.onRotationPlay;
        this.plugin.OnDeselect = this.onRotationStop;
        this.plugin.OnClick = this.onColorChange;

        const title = this.plugin.GetTitle();
        console.log("title", title);
        /*this.setState({
            title: title,
        })*/
    }

    onRotationPlay() {
        console.log("rotation play")
    }
    onRotationStop() {
        console.log("rotation stop")
    }
    onColorChange(color) {
        console.log("change color: " + color)
    }

    onPlay() {
        this.plugin.Play();
    }
    onStop() {
        this.plugin.Stop();
    }

    onSetColor() {
        this.plugin.SetTankColor("#55aa22");
    }

    handleChange(e) {

        console.log(e.target.value);

        this.setState({
            title: e.target.value,
        })

    }

    onSetTitle() {
        this.plugin.SetTitle("PANZER PANTS");
    }

    handleProperty() {

        const rank = this.plugin.GetTankProperty("Rank");
        const power = this.plugin.GetTankProperty("Power");
        const armor = this.plugin.GetTankProperty("Armor");
        const speed = this.plugin.GetTankProperty("Speed");

        console.log("property", rank, power, armor, speed);

    }

    render() {
        return (
            <>
                <div ref={(el) => this.appContainer = el}>

                    <h4>Description</h4>

                    <input type="text" onChange={this.handleChange} defaultValue={this.state.title} />
                    <br />

                    <h4>Operations</h4>

                    <button onClick={this.onPlay}>PLAY</button><br />
                    <button onClick={this.onStop}>STOP</button><br />
                    <button onClick={this.onSetTitle}>SET TITLE</button><br />

                    <button onClick={this.onSetColor}>SET COLOR</button><br />

                    <button onClick={this.handleProperty}>GET PROPERTY</button><br />

                </div>
                <style jsx>
                {`
                    div {
                        background-color: lightcyan;
                        width: 100%;
                        height: 100%;
                    }
                `}
                </style>
            </>
        )
    }
}
